    const url = `/${urlTitle}/${blog.blogId}`;
    const id  = params.id
    const res = await fetch(`https://us-central1-resume-builder-startup.cloudfunctions.net/api/getBlog/${id}`)
    const data = await res.json()
    // console.log(data);
    return { props: { blog: data } };
  }

