    const res = await fetch("https://us-central1-resume-builder-startup.cloudfunctions.net/api/getAllBlog")
    const data = await res.json()
    return{ 
        paths:data? data.map((blog) =>({
            params:{
                id:blog.blogId,
                title:blog.title
            }
        })) : [],
        fallback:true
      };
  }
