   GET_ALL_BLOG, GET_BLOG} from "../Types";

export const postBlog = (newBlog) => async (dispatch) => {
    dispatch({ type: LOADING_UI});
  const res = axios.post('/postBlog', newBlog)   
      dispatch({
        type:POST_BLOG,
        payload:res.data
      });
      dispatch({ type: CLEAR_ERRORS })   
