import React from 'react';
import dynamic from 'next/dynamic';

const QuillNoSSRWrapper = dynamic(
  () => import('./RenderReactQuill'),
  { ssr: false, loading: () => <p>Loading WYSIWYG</p> },
)

class RenderQuill extends React.Component {
    state={
        render:false
    }
    componentDidMount(){
        this.setState({
            render:true
        })
    }
  render () {
    const { input, label, type } = this.props
    return (
       <>
       {
           this.state.render ?
           <QuillNoSSRWrapper input={input} label={label} type={type}/>:
           <div>LOADING</div>
       }
       </>
    )
  }
}


export default RenderQuill
