import React, { Component } from 'react';
import fetch from "isomorphic-unfetch";

// export async function getStaticPaths(){

// }

const User = ({ data }) =>{
    return (
        <>
         {
            //  data.blogPosts.map((b)=>(
            //      <h2>{b.title}</h2>
            //  ))
            console.log(data)
         }
        </>
    )
}
// export async function getStaticProps ({params}) {
//     // In real world we can use our ctx.query.id here to fetch our  api and get our data on SSR
//     return(
//         <h1>USER</h1>
//     )
//  };

export const getServerSideProps = async ({params}) =>{

    const res = await fetch(`https://us-central1-resume-builder-startup.cloudfunctions.net/api/blogUser/${params.id}`)
    const data = await res.json();
   
    return{
        props:{
            data
        }
    }
    
}
export class UserName extends Component {
    render() {
        return (
            <div>
                <User/>
            </div>
        )
    }
}

export default UserName
